import { createStore } from "redux";

// action 타입 상수 정의
export const CHANGE = "CHANGE"

// redux state의 초기값 정의
const initialCourse = {
    thumbnail: "",
    title: "",
    owner: ""
};

// action 객체를 반환하기 위한 함수
export const changeCourse = (thumb, tit, own) => {
    return {
        type: CHANGE,
        thumbnail: thumb,
        title: tit,
        owner: own
    }
};

export function setCourse(state = initialCourse, action) {
    switch (action.type) {
        case CHANGE:
            return {
                ...state,
                thumbnail: state.thumbnail,
                title: state.title,
                owner: state.owner
            }
        default:
            return state;
    }
}

export const store = createStore(setCourse);

console.log(store.getState())

