import { useContext } from 'react';
import './CurationItem.css'
import { CurationContext } from './Curation';

function CurationItem() {
    const { title, src } = useContext(CurationContext)
    return (
        <div className="curation-item">
            <div className="curation-img">
                <img src={src}></img>
            </div>
            <div className="curation-title">
                {title}
            </div>
        </div>
    );
};

export default CurationItem;
