import './Container.css';
import { useState, useEffect } from 'react';
// Î∞∞ÎÑà Ïù¥ÎØ∏ÏßÄ import
import banner1 from '../media/banner1.png';
import banner2 from '../media/banner2.gif';
import banner3 from '../media/banner3.png';
import { FaAngleLeft, FaAngleRight, FaPause } from "react-icons/fa";

function Container() {
    const bannerList = [banner1, banner2, banner3];
    // stateÎ•º Ïù¥Ïö©ÌïòÏó¨, Ïù∏Îç±Ïä§Ïùò Î≥ÄÌôîÏóê Îî∞Îùº Î≥¥Ïó¨Ï£ºÎäî Î∞∞ÎÑàÎ•º Îã¨Î¶¨Ìï† Í≤ÉÏûÑ.
    const [currentNum, setCurrentNum] = useState(0);

    useEffect(() => {
        const intervalid = setInterval(() => {
          setCurrentNum((currentNum + 1) % bannerList.length);
        }, 5000);
        return () => clearInterval(intervalid);
      }, [currentNum, bannerList.length]);

    // event.targetÍ≥º event.currentTargetÏùò Ï∞®Ïù¥
    /*
    function handleClick(event) {
        console.log(event.target); // Î≤ÑÌäº ÏöîÏÜå
        console.log(event.currentTarget); // div ÏöîÏÜå(ÎßåÏïΩ ÏûêÏãùÏöîÏÜåÍ∞Ä ÏûàÎäî Í≤ÉÏù∏Îç∞ click Ïù¥Î≤§Ìä∏Î•º Íµ¨ÌòÑÌïòÍ≥† Ïã∂Îã§Î©¥ Ïù¥Í≤ÉÏùÑ Ïì¥Îã§)
            }

        return (
            <div onClick={handleClick}>
                <button>Click me</button>
            </div>
        );
     */
    const onClick = (event) => {
        console.log(`event ${event}`);
        let clicked = event.currentTarget.id;
        console.log(`clicked ${clicked}`);
        if (clicked === "arrow-left") {
            console.log("ÏôºÏ™Ω")
            setCurrentNum((currentNum - 1 + bannerList.length) % bannerList.length);
        } else if (clicked === "arrow-right") {
            console.log("Ïò§Î•∏Ï™Ω")
            setCurrentNum((currentNum + 1) % bannerList.length);
        }
    };

    const onChipClick = (event) => {
        console.log(`event ${event}`);
        let chip = event.target.id;
        event.target.style.color="green"
        event.target.style.border="2px solid green"
        if (chip === '1') {
            setCurrentNum(0);
        } else if (chip === '2') {
            setCurrentNum(1);
        } else {
            setCurrentNum(2);
        }
    }

    return(
        <div className="container">
            <div className="banner-container">
                <div className='banner'>
                    {bannerList.map((slide, index) => (
                        <img
                            key={index}
                            src={slide}
                            alt={`Banner ${index}`}
                            style={{ display: index === currentNum ? 'block' : 'none' }}
                        />
                    ))}
                </div>
            </div>
            <div className='banner-controller'>
                <div className='index-controller'>
                    {currentNum + 1}/{bannerList.length}
                    <span id="arrow-left" onClick={onClick}>
                        <FaAngleLeft />
                    </span>
                    <span id="pause" onClick={onClick}>
                        <FaPause />
                    </span>
                    <span id="arrow-right" onClick={onClick}>
                        <FaAngleRight />
                    </span>
                </div>
                <div id='separator'>
                </div>
                <div>
                    <span className='chip-item' id='1' onClick={onChipClick}>7thÏ∞ΩÎ¶ΩÍ∏∞ÎÖêÏùºüçÄ</span>
                    <span className='chip-item' id='2' onClick={onChipClick}>Î°úÎìúÎßµ üå±</span>
                    <span className='chip-item' id='3' onClick={onChipClick}>Î¨¥Î£å Î∂ÄÌä∏Ï∫†ÌîÑ</span>
                </div>
            </div>
        </div>
    );
};

export default Container;
