import { useSelector } from 'react-redux';
import { changeCourse, setCourse, store } from '../store';
import './Course.css'

function Course({ thumbnail, courseTitle, courseOwner, rating, price }) {

    // 네트워크 통신으로 thumbnail, courseTitle, courseOwner, price를 가져왔다고 가정

    store.dispatch(setCourse({thumbnail, courseTitle, courseOwner}, changeCourse));


    // react redux에 있는 useSelector
    const img = useSelector(state => state.thumbnail);
    const title = useSelector(state => state.title);
    const owner = useSelector(state => state.owner);


    return (
        <div className="course">
            <div className="course-thumbnail">
                <img src={img} alt="thumbnail"/>
            </div>
            <span id='title'>{title}</span>
            <span id='owner'>{owner}</span>
            <span id='rating'>
